import fetch from 'isomorphic-fetch';

const Index = (data) => {

    return ( <
        div >

        <
        ul > {
            data.pageData.map(news => ( <
                li key = {
                    news.id
                } > {
                    news.title
                } < /li>
            ))
        } <
        /ul> <
        /div>
    )

}

Index.getInitialProps = async () => {

    try {
        const response = await fetch('https://node-hnapi.herokuapp.com/news?page=1')
            .then((response => response.json()))
        return {
            pageData: response
        };
    } catch (err) {
        console.log(err);
    }
}

export default Index;
