import fetch from 'isomorphic-fetch';
import Error from 'next/error';
import { useState } from 'react';

const Index =({ data, error })=> {

    const [getNews, setNews] = useState(data)
    if(getNews === ''){
        <h2 style={{ textAlign:"center", color: "red" }}> {error.message} </h2>    
    }

    return (
        <div>
            <ul>
                {data.map(news => (
                    <li key={news.id}> {news.title} </li>
                ))}
            </ul>
        </div>
    )

}

Index.getInitialProps = async ()=> {

    let response;
    let errMassage;
    try {
        response = await fetch('https://node-hnapi.herokuapp.com/newge=1')
        .then((response => response.json())) 
        console.log(response);        
    }
    catch(err) {
        console.log(err);
        response = [];
        errMassage = err;
    }

    return { res: {
        data: response,
        error: errMassage
    } };
}

export default Index;
