import fetch from "isomorphic-fetch";

const Img = ({ data: data }) => {

  return (
    <div>
      <h2> Images </h2>
      {data.map(img =>(
          <img loading="lazy" src={img.flag}></img>
      ))}
    </div>
  );
};

Img.getInitialProps = async () => {
  try {
    const images = await fetch(
      "https://restcountries.eu/rest/v2/all"
    );
    const res = await images.json();
    return { data: res };
  } catch (err) {
    console.log("Something went wrong!", err);
  }
};

export default Img;
