import fetch from 'isomorphic-fetch';
import Error from 'next/error';
import { useState } from 'react';

const Index =({ data: data, error: err })=> {

    const [getNews, setNews] = useState(data)

    console.log(getNews, '===')
    if(getNews.length === 0){
        <h2 style={{ textAlign:"center", color: "red" }}> {err.message} </h2>    
    }
    console.log(data)

    return (
        <div>
            <ul>
                {data.map(news => (
                    <li key={news.id}> {news.title} </li>
                ))}
            </ul>
        </div>
    )

}

Index.getInitialProps = async ()=> {

    let response;
    try {
        response = await fetch('https://node-hnapi.herokuapp.com/newge=1')
        .then((response => response.json())) 
        console.log(response);        
    }
    catch(err) {
        console.log(err);
        response = [];
    }

    return { data: response, error: err };
}

export default Index;
